// src/App.jsx
import React, { useEffect } from "react";
import { 
  useDispatch, // lets a component send actions ("add country to visited")
  useSelector // lets a component read data from the store
} from "react-redux";
import MapChart from "./components/MapChart";
import {
  toggleVisited,
  toggleWishlist,
  highlightAllWishlist,
} from "./redux/mapSlice";
import allCountryCodes from "./allCountryCodes";

const App = () => {
  const dispatch = useDispatch();
  const { visitedCountries, wishlistCountries } = useSelector(state => state.map);
  console.log("✅ allCountryCodes:", allCountryCodes);
  useEffect(() => {
    if (wishlistCountries.length === 0 && visitedCountries.length === 0) {
      dispatch(highlightAllWishlist(allCountryCodes));
      console.log("Dispatched highlightAllWishlist with allCountryCodes:", allCountryCodes);
    }
  }, [dispatch, wishlistCountries.length, visitedCountries.length]);

  return (
    <div style={{ padding: "2rem" }}>
      <h1 style={{ textAlign: "center", marginBottom: "2rem" }}>
        Where Do You Want to Go?
      </h1>

      <div style={{ display: "flex", gap: "2rem", justifyContent: "center", flexWrap: "wrap" }}>
        <MapChart
          mapName="Visited"
          countries={visitedCountries}
          onCountryClick={(code) => dispatch(toggleVisited(code))}
        />
        <MapChart
          mapName="Want to Go"
          countries={wishlistCountries}
          onCountryClick={(code) => dispatch(toggleWishlist(code))}
        />
      </div>
    </div>
  );
};

export default App;
