// scripts/generateAllCountryCodes.js
import fetch from "node-fetch";
import fs from "fs";

const geoUrl = "https://cdn.jsdelivr.net/npm/world-atlas@2/countries-110m.json";

fetch(geoUrl)
  .then(res => res.json())
  .then(async (data) => {
    const topojson = await import("topojson-client");
    const countries = topojson.feature(data, data.objects.countries).features;
    const codes = countries.map(c => c.properties.ISO_A3).filter(Boolean);

    fs.writeFileSync("./src/allCountryCodes.js", `export default ${JSON.stringify(codes, null, 2)};\n`);
    console.log("✅ allCountryCodes.js generated with", codes.length, "codes");
  });
