import { useSelector, useDispatch } from "react-redux";
import { toggleVisited, toggleWishlist, highlightAllWishlist } from "./redux/mapSlice";
import MapChart from "./components/MapChart";
import { useEffect } from "react";
import allCountryCodes from "./allCountryCodes"; // list of all ISO_A3 codes

const App = () => {
  const visited = useSelector((state) => state.map.visitedCountries);
  const wishlist = useSelector((state) => state.map.wishlistCountries);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(highlightAllWishlist(allCountryCodes));
  }, [dispatch]);

  return (
    <div style={{ display: "flex", gap: "2rem", padding: "2rem", flexWrap: "wrap" }}>
      <MapChart mapName="Visited" countries={visited} onCountryClick={(c) => dispatch(toggleVisited(c))} />
      <MapChart mapName="Want to Go" countries={wishlist} onCountryClick={(c) => dispatch(toggleWishlist(c))} />
    </div>
  );
};

export default App;
