// src/components/MapChart.js
import React from "react";
import { ComposableMap, Geographies, Geography } from "react-simple-maps";
import { useSelector, useDispatch } from "react-redux";
import { toggleCountry } from "../redux/mapSlice";

const geoUrl =
  "https://cdn.jsdelivr.net/npm/world-atlas@2/countries-110m.json";

// Props come in as an object — so destructure it here
const MapChart = ({ mapName }) => {
  const highlighted = useSelector((state) => state.map.highlightedCountries);
  const dispatch = useDispatch();

  return (
    <div
      style={{
        width: "550px", // Wider container
        padding: "1rem",
        border: "1px solid #ccc",
        borderRadius: "12px",
        background: "#f9f9f9",
        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
      }}
    >
      <h2 style={{ textAlign: "center", marginBottom: "1rem" }}>{mapName}</h2>
      <ComposableMap>
        <Geographies geography={geoUrl}>
          {({ geographies }) =>
            geographies.map((geo) => {
              const isHighlighted = highlighted.includes(geo.properties.ISO_A3);
              return (
                <Geography
                  key={geo.rsmKey}
                  geography={geo}
                  onClick={() =>
                    dispatch(toggleCountry(geo.properties.ISO_A3))
                  }
                  style={{
                    default: {
                      fill: isHighlighted ? "#FF5722" : "#D6D6DA",
                      outline: "none",
                    },
                    hover: {
                      fill: "#F53",
                      outline: "none",
                    },
                    pressed: {
                      fill: "#E42",
                      outline: "none",
                    },
                  }}
                />
              );
            })
          }
        </Geographies>
        projectionConfig={{
          scale: 150, // You can tweak this scale value for size
        }}
        width={500}   // Increase width
        height={300}  // Increase height
        style={{ width: "100%", height: "auto" }} // Responsive
      </ComposableMap>
    </div>
  );
};

export default MapChart;
