import { createSlice } from "@reduxjs/toolkit";

const mapSlice = createSlice({
  name: "map",
  initialState: {
    highlightedVisitedCountries: []
  },
  reducers: {
    toggleCountry: (state, action) => {
      const code = action.payload;
      if (state.highlightedVisitedCountries.includes(code)) {
        state.highlightedVisitedCountries = state.highlightedVisitedCountries.filter(
          (c) => c !== code
        );
      } else {
        state.highlightedVisitedCountries.push(code);
      }
    },
  },
});

export const { toggleCountry } = mapSlice.actions;
export default mapSlice.reducer;
